import React , {useEffect, useState} from 'react'
import Slider from "react-slick";
import Item from './Item';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import axios from "axios"
export default function Chiffre() {
    const prices = fetch('../../app flask/prices.json')
    const [data, setData] = useState(Object.values({"ETH": "ETH 3480.7890625 $ -1", "ADA": "ADA 2.3965888023376465 $ 1", "HEX": "HEX 0.4511164724826813 $ 1", "BNB": "BNB 415.9463806152344 $ 1", "USDT": "USDT 0.9991824626922607 $ -1", "SOL1": "SOL1 162.31631469726562 $ 1", "XRP": "XRP 1.0864537954330444 $ -1", "DOGE": "DOGE 0.24258559942245483 $ -1", "DOT1": "DOT1 35.346160888671875 $ -1", "USDC": "USDC 0.9990710020065308 $ -1", "UNI3": "UNI3 24.68401527404785 $ 1", "LINK": "LINK 28.864871978759766 $ 1", "BCH": "BCH 634.588134765625 $ 1", "LTC": "LTC 182.6259307861328 $ -1", "ALGO": "ALGO 2.051389694213867 $ 1", "LUNA1": "LUNA1 35.77714920043945 $ 1", "ICP1": "ICP1 59.68444061279297 $ -1"}))
    const [time, setTime] = useState()
    const getData = ()=>{
        axios.get("http://127.0.0.1:5000/")
        .then(data=>{
            setData(Object.values(data.data))
            setTimeout(() => {
                setTime(Math.random())
            }, 30000);
            
        })
        .catch(error=>{
            setTimeout(() => {
                setTime(Math.random())
            }, 30000);
        })
    }
    const Repetation = ()=> setInterval(getData(),30000)

    useEffect(() => {
        console.log(prices)
        Repetation()
    },[time])
    
    const settings = {
        slidesToShow: 8,
        slidesToScroll: 1,
        arrows: false,
        dots: false,
        autoplay: true,
        speed:300,
        className: `chiffres-container`,
        responsive: [
            {
                breakpoint: 1200,
                settings: {
                    slidesToShow: 7
                }
            },
            {
                breakpoint: 1070,
                settings: {
                    slidesToShow: 5
                }
            },
           
            {
                breakpoint: 700,
                settings: {
                    slidesToShow: 5
                }
            },
            {
                breakpoint: 500,
                settings: {
                    slidesToShow: 4
                }
            },
            {
                breakpoint: 300,
                settings: {
                    slidesToShow: 2
                }
            },
        ]
    }
    return (
        <div >
            <div>
                <div className="row">
                    <div className="chiffres-container"  style={{backgroundColor:"black"}}>
                        <Slider {...settings}>
                            {
                                data.map((item,index) => (
                                    <Item key={index} symbole={item.split(" ")[2]} value={item.split(" ")[0]} counter={parseFloat(item.split(" ")[1]).toFixed(4)} fleche={item.split(" ")[3]}/>
                                ))
                            }
                        </Slider>
                    </div>
                </div>
            </div>
        </div>
    )
}
