import React , {useEffect, useState} from 'react'
import Slider from "react-slick";
import Item from './Item';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import axios from "axios"
export default function Chiffre(props) {
    const [data, setData] = useState([])
    function Repetation(){
        axios.get("http://127.0.0.1:5000/")
        .then(data=>{
            setData(Object.values(data.data))
            console.log('yew')
            props.charger(true)
        })
    }
    useEffect(() => {
        props.charger(false)
        setInterval(Repetation(),60000)
    }, [])
    
    const settings = {
        slidesToShow: 5,
        slidesToScroll: 1,
        arrows: false,
        dots: false,
        autoplay: true,
        speed:300,
        className: `chiffres-container`,
        responsive: [
            {
                breakpoint: 1200,
                settings: {
                    slidesToShow: 4
                }
            },
            {
                breakpoint: 700,
                settings: {
                    slidesToShow: 3
                }
            },
            {
                breakpoint: 500,
                settings: {
                    slidesToShow: 2
                }
            },
            {
                breakpoint: 300,
                settings: {
                    slidesToShow: 1
                }
            },
        ]
    }
    return (
        <div >
            <div>
                <div className="row">
                    <div className="chiffres-container"  style={{backgroundColor:"black"}}>
                        <Slider {...settings}>
                            {
                                data.map((item,index) => (
                                    <Item key={index} symbole={item.split(" ")[2]} value={item.split(" ")[0]} counter={parseFloat(item.split(" ")[1]).toFixed(4)} fleche={item.split(" ")[3]}/>
                                ))
                            }
                        </Slider>
                    </div>
                </div>
            </div>
        </div>
    )
}
