import React , {useEffect, useState} from 'react'
import Slider from "react-slick";
import Item from './Item';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import axios from "axios"
import prices from '../JsonFile/prices.json'
export default function Chiffre() {
    const fs = require('file-system')
    const editJsonFile = require("edit-json-file");
    let file = editJsonFile(`../JsonFile/prices.json`);
    const [data, setData] = useState(Object.values(prices))
    const [time, setTime] = useState()
    const getData = ()=>{
        axios.get("http://127.0.0.1:5000/")
        .then(data=>{
            setData(Object.values(data.data))
            
            setTimeout(() => {
                setTime(Math.random())
            }, 30000);
            file.set("data", JSON.stringify(data.data))
            file.save()
            console.log('test')
            console.log(file.get("data")) 
        })
        .catch(error=>{
            setTimeout(() => {
                setTime(Math.random())
            }, 30000);
        })
    }
    const Repetation = ()=> setInterval(getData(),30000)

    useEffect(() => {
        Repetation()
    },[time])
    
    const settings = {
        slidesToShow: 5,
        slidesToScroll: 1,
        arrows: false,
        dots: false,
        autoplay: true,
        speed:300,
        className: `chiffres-container`,
        responsive: [
            {
                breakpoint: 1200,
                settings: {
                    slidesToShow: 4
                }
            },
            {
                breakpoint: 700,
                settings: {
                    slidesToShow: 3
                }
            },
            {
                breakpoint: 500,
                settings: {
                    slidesToShow: 2
                }
            },
            {
                breakpoint: 300,
                settings: {
                    slidesToShow: 1
                }
            },
        ]
    }
    return (
        <div >
            <div>
                <div className="row">
                    <div className="chiffres-container"  style={{backgroundColor:"black"}}>
                        <Slider {...settings}>
                            {
                                data.map((item,index) => (
                                    <Item key={index} symbole={item.split(" ")[2]} value={item.split(" ")[0]} counter={parseFloat(item.split(" ")[1]).toFixed(4)} fleche={item.split(" ")[3]}/>
                                ))
                            }
                        </Slider>
                    </div>
                </div>
            </div>
        </div>
    )
}
