import React from "react";
import "../assets/css/item.css"
function Item(props) {
  return (
        <div className="stockItem col-6 col-md-5 text-center"  style={{marginLeft:"auto",marginRight:"auto"}}>
                {(props.fleche === "1") ?<i class="fa fa-caret-up" aria-hidden="true" style={{color:"green",fontSize:"25px"}}></i>
                :<i class="fa fa-caret-down" aria-hidden="true" style={{color:"red",fontSize:"25px"}}></i>}
                <p className="Value">{props.value}  <span id="Counter">{props.counter}</span> {props.symbole}</p>
        </div>
  );
}

export default Item;
