import React , {useEffect, useState} from 'react'
import Slider from "react-slick";
import Item from './Item';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import axios from "axios"
export default function Chiffre() {
    const [data, setData] = useState([])
    //myGFG({pic:"ADA 2.24154546 $ -1",question:"BTC 2.24154546 $ +1",answer:"USD 2.24154546 $ -1",wrongAnswer1:"TRT 2.24154546 $ -1",wrongAnswer2:"HREC 2.24154546 $ -1"})
    function myGFG(obj) {
        var res = [];
          
        for(var i in obj)
            res.push(obj[i]);
        return res 
    }
    function Repetation(){
        axios.get("http://127.0.0.1:5000/")
        .then(data=>{
            setData(Object.values(data.data))
            console.log(data.data)
        })
    }
    useEffect(() => {
        setInterval(Repetation(),60000)
    }, [])
    
    const settings = {
        slidesToShow: 5,
        slidesToScroll: 1,
        arrows: false,
        dots: false,
        autoplay: true,
        speed:300,
        className: `testimonial-content--3 testimonial-grid`,
        responsive: [
            {
                breakpoint: 1200,
                settings: {
                    slidesToShow: 2
                }
            },
            {
                breakpoint: 992,
                settings: {
                    slidesToShow: 1
                }
            },
        ]
    }
    return (
        <div >
            <div>
                <div className="row">
                    <div className=""  style={{backgroundColor:"black"}}>
                        <Slider {...settings}>
                            {
                                data.map((item,index) => (
                                    <Item key={index} symbole={item.split(" ")[2]} value={item.split(" ")[0]} counter={parseFloat(item.split(" ")[1]).toFixed(4)} fleche={item.split(" ")[3]}/>
                                ))
                            }
                        </Slider>
                    </div>
                </div>
            </div>
        </div>
    )
}
